package main

import (
	"fmt"
	"github.com/go-redis/redis"
	"strconv"
)

func connect_redis() *redis.Client {
	client := redis.NewClient(&redis.Options{
		Addr:     "localhost:6379",
		Password: "", // no password set
		DB:       0,  // use default DB
	})

	return client
}

func main() {
	cache := connect_redis()
	for i := 0; i < 10000; i++ {
		cache.Set("key"+strconv.Itoa(i), value_5K, 0)
	}

	fmt.Println("hello world")
}

var value10 = "123456789012345678901234567890"

var value_5K = ""
